body {
    font-family: Arial, sans-serif;
	margin:0;
}

a{
	color: inherit; 
	text-decoration: inherit;
}

/* ####-Main landing page-####*/

.header{
	display: flex;
	justify-content: center;
	height:33vh;
	padding-top:12%;
	width:100%;
	justify-self: center;
	background: linear-gradient( rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5) ),  url(../assets/imgs/hero-img.jpg);
	background-position: center;
	background-size: cover;
	color:white;
	font-size: xx-large;
	letter-spacing: 0.03rem;
}

/*---Main options container styling---*/
.main-options-container{
	display: flex;
	flex-direction: row;
	justify-content: space-around;
	padding-top: 3%;
}

  /*---Top header---*/
  .header-info{
    display:flex;
    flex-direction: row;
    position:absolute;
    top:0;
    right:0;
    padding:0.2em;
  }

  .header-info p{
    margin:0;
    font-size:20px;
    margin-right:0.2em;
  }

  .header-info button{
    border:1px solid white;
    border-radius: 0.2em;
    background: #9FA8A3;
    color:white;
  }

  .header-info button:hover{
    cursor: pointer;
    opacity: 0.8;
  }

/*---Footer---*/
.footer{
	width: 90%;
	background: #9FA8A3;
	height: 13vh;
	margin-top:40px;
	display: flex;
	flex-direction: row;
	justify-content: space-between;
	padding-left: 5%;
	padding-right: 5%;
}

.footer-container{
	margin-top:40px;
	font-weight: 600;
	color:white;
	margin-left:auto;
	margin-right: auto;
}

.footer-link:hover{
	opacity: 0.7;
	cursor: pointer;
}
/*####-Secondary Pages-####*/

/*---Home button in the hotels, restaurants and cafees pages---*/
.home-btn{
	margin-left: 2rem;
}
.home-btn:hover{
	text-decoration: underline;
	cursor: pointer;
}

/*---Filter---*/
.filter-and-options-container{
	display: flex;
	flex-direction: row;

}
.filtermenu{
	background: #9FA8A3;
	min-width: 20%;
	padding: 0.4rem;
	height: 95vh;
}

.filter-title{
	font-weight: bold;
}

/*---Secondary options container styling---*/
.secondary-options-container{
	margin:2rem;
	display: grid;
	grid-template-columns: repeat(3, 1fr);
  	gap: 20px;
	z-index:1;
}

.secondary-options-container .option-box{
	width:100%;
	min-height: 45vh;
}

.secondary-options-container .option-box h2{
	font-size: x-large;
	font-weight: bolder;
	text-decoration: none;
}

/*---Option Box styling---*/

.option-box{
	padding:0.2rem;
	border-radius: 0.5rem;
	background:whitesmoke;
	box-shadow: 10px 5px 5px rgba(0,0,0,0.2);
	width:30%;
	height:50vh;
	transition: all .2s ease-in-out; 
	overflow-y: auto;
}
.option-box-main{
	height:35vh;
}

.option-box h2{
	font-weight: 300;
	font-size: large;
	margin: 0.2rem;
}

.option-box p{
	margin: 0.2rem;
	font-weight: 100;
	opacity: 0.75;

}

.option-box h2 {
	font-size: xx-large;
	text-decoration: underline;
}

.option-box-img{
	border-top-left-radius: 0.5rem;
	border-top-right-radius: 0.5rem;
	width:100%;
	height:50%;
	max-height: 400px;
	object-fit: cover;
}

.cafe-restaurant-serving{
	opacity: 0.4;
	display: flex;
	flex-direction: row;
	margin-top: -0.7rem;
}

.title-location-container {
	display: flex;
	flex-direction: row;
	justify-content: space-between;
}

.location-with-icon{
	display: flex;
	flex-direction: row;
}

.option-box-info-container {
padding: 0.5rem;	
}

.option-box:hover{
	transform: scale(1.05); 
	cursor: pointer;
}

/*---Modal styling---*/

.info-modal{
	background:rgba(0, 0, 0, 0.5);
	position: fixed;
	top: 0;
	bottom: 0;
  	left: 0;
  	right: 0;
	opacity: 0;
	height:100%;
	visibility: hidden;
	z-index:99;
	&:target{
		visibility: visible;
		opacity: 1;
	}	
}

.modal-content{
	position:absolute;
	background: white;
	margin:10% 20%;
	height:75vh;
    overflow-y: auto;
	padding: 1rem;
	border-radius: 2rem;
	z-index:3;
	width:60%;
}

.modal-link{
	color:grey;
}

.modal-link:hover{
	cursor: pointer;	
}

.modal-img{
	border-top-left-radius: 2rem;
	border-top-right-radius: 2rem;
	width:100%;
	height:70%;
	max-height: 400px;
	object-fit: cover;
}

.close-modal{
	position:absolute;
	width:100%;
	height:100%;
	cursor: default;
	z-index:2;
}
.info-tag{
	padding:0.2em;
	background:#9FA8A3;
	display:flex;
	width:fit-content;
	margin-left:auto;
	margin-top:0.1em;
	border-radius:0.2em;
  }

  /*---Rating---*/
  .star-img{
    height:20px;
    width:auto;
  }

  .star-img-user{
    height:20px;
    width:auto;
    opacity:0.4;
  }

 .star-img-user:hover,
 .star-img-user:hover + .star-img-user,
 .star-img-user:hover + .star-img-user + .star-img-user,
 .star-img-user:hover + .star-img-user + .star-img-user + .star-img-user,
 .star-img-user:hover + .star-img-user + .star-img-user + .star-img-user + .star-img-user {
    opacity: 1;
    cursor: pointer;
}

  .rating-container{
    display:flex;
    flex-direction: row;
    padding-top:1em;
    padding-bottom:1em;
  }

/*---Comment---*/
  .comment{
    padding:0.5em;
    margin:0.2em;
    background:#9FA8A3;
  }

  .new-comment{
    padding:0.5em;
    margin:0.2em;
    display:flex;
    justify-content: space-between;
  }

  .new-comment input{
    width:65%;
    padding:1em;
  }

  .new-comment button {
    width:25%;
    background:#9FA8A3;
    border:1px solid white;
    border-radius: 0.4em;
    padding:1em;
  }


/*---Media query for various screensizes for responsive design---*/
@media only screen and (max-width: 1000px) {
	.secondary-options-container{
		grid-template-columns: repeat(2, 1fr);
	}
  }

@media only screen and (max-width: 700px) {
	.main-options-container{
		flex-direction: column;
		align-items: center;
	}

	.secondary-options-container{
		grid-template-columns: repeat(1, 2fr);
	}

	.secondary-options-container .option-box{
		min-height:320px;
	}

	.main-options-container .option-box{
		width:80%;
		margin:2rem;

	}
	.filtermenu{
		font-size: small;
	}
	.filtermenu input{
		margin-bottom:0.5rem;
	}
	.footer{
		flex-direction: column;
		height:fit-content;
		align-items: center;
	}
  }
