	/* RATING */ 
	function generateStarImages() {
        let starImagesHTML = '';
        for (let i = 0; i < 5; i++) {
            starImagesHTML += `<img src=${startImg} class="star-img-user" data-index="${i + 1}">`;
        }
        return starImagesHTML;
    }

	function updateRating(hotelId, rating, ratingContainer) {
		const hotelRef = firestore.collection('hotels').doc(hotelId);
		const user = JSON.parse(localStorage.getItem('user'));
		const userId = user ? user.uid : null; 

		if (!userId) {
			console.error('User not logged in!');
			return;
		}

		const userRatingRef = firestore.collection('users').doc(userId);

		let hotelRatings;
		let userRatings;

		hotelRef.get().then(doc => {
			if (doc.exists) {
				hotelRatings = doc.data().rating || [];
			} else {
				console.error('Hotel document does not exist!');
				return;
			}

			return userRatingRef.get();
		}).then(doc => {
			if (doc.exists) {
				userRatings = doc.data()[`hotels-${hotelId}`] || [];
			} else {
				console.error('User document does not exist!');
				return;
			}

			hotelRatings.push(rating);

			userRatings.push(rating);

			return Promise.all([
				hotelRef.update({ rating: hotelRatings }),
				userRatingRef.update({ [`hotels-${hotelId}`]: userRatings })
			]);
		}).then(() => {
			console.log('Rating updated successfully for both hotel and user!');
			if (ratingContainer) {
				ratingContainer.innerHTML = 'Thank you for your vote!';
			}
		}).catch(error => {
			console.error('Error updating rating:', error);
		});
	}





	function calculateAverageRating(ratings) {
		if (ratings.length === 0) return "-"; 
		const totalRating = ratings.reduce((acc, rating) => acc + rating, 0);
		const averageRating = totalRating / ratings.length;
    	return averageRating.toFixed(1);
	}

	/* COMMENTS */
	function renderComments(commentsArray) {
		let html = ''; 

		for (let i = 0; i < commentsArray.length; i += 2) {
			const [username, comment] = commentsArray.slice(i, i + 2);
			html += `<div class="comment"><strong>${username}: </strong><span>${comment}</span></div>`;
		}

		return html; 
	}

	function addNewComment(hotelName) {
		const user = JSON.parse(localStorage.getItem('user'));
		let username = ''; 
		if (user) {
			username = user.displayName || user.email;
		}

		const commentInput = document.getElementById('comment');

		if (username && commentInput) {
			const commentText = commentInput.value.trim();
			commentInput.value = ''; 

			if (commentText !== '') {
				const hotelRef = firestore.collection('hotels').doc(hotelName);

				return firestore.runTransaction(transaction => {
					return transaction.get(hotelRef).then(doc => {
						if (!doc.exists) {
							throw new Error('Hotel document does not exist!');
						}

						const updatedComments = [...doc.data().comments, username, commentText];

						transaction.update(hotelRef, { comments: updatedComments });

						return updatedComments;
					});
				})
				.catch(error => {
					console.error('Error adding comment:', error);
				});
			} else {
				console.warn('Comment cannot be empty!');
			}
		}
	}

