				<input type="text" id="comment" placeholder="Write a comment...">
				<button id="add-comment-button">Add Comment</button>
			</div>
		`;
	}

	/* FILTER */
	function isHotelMatchingFilters(hotel) {
		const filterCheckboxes = document.querySelectorAll('.filter-checkbox');
		let anyChecked = false;

		for (let checkbox of filterCheckboxes) {
			if (checkbox.checked && hotel.location === checkbox.id) {
				return true;
			}
			if (checkbox.checked) {
				anyChecked = true;
			}
		}

		if (!anyChecked) {
			return true;
		}

		return false;
	}

